import Web3 from "web3";

import metaCoinArtifact from "../../build/contracts/MetaCoin.json";
import twentyOneArtifact from "../../build/contracts/TwentyOneGame.json";

var GameContract;
var bytecode = '';
var abi = twentyOneArtifact.abi;

// GameContract = Contract(twentyOneArtifact);

var accounts;
var account;

const App = {
  web3: null,
  account: null,
  meta: null,
  game: null,

  start: async function() {
    const { web3 } = this;

    // get accounts
    accounts = await web3.eth.getAccounts();
    account = accounts[0];
    
    GameContract = new web3.eth.Contract(abi);

    try {
      // get contract instance
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = metaCoinArtifact.networks[networkId];
      this.meta = new web3.eth.Contract(
        metaCoinArtifact.abi,
        deployedNetwork.address,
      );

      this.refreshBalance();

    } catch (error) {
      console.error("Could not connect to contract or chain.");
    }

    try {

      // get contract instance
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = twentyOneArtifact.networks[networkId];
      GameContract = new web3.eth.Contract(
        twentyOneArtifact.abi,
        //deployedNetwork.address,
      );

    } catch (error) {
      console.error("Could not connect to contract or chain.");
    }
  },

// TwentyOneGame Contract Functions 
  
  //this function starts a new instance of a game
  startGame: async function() {
    const { web3 } = this;
    
    console.log(GameContract);

    GameContract.deploy( {
      data: bytecode
    }).send({
      from: account,
      gas: 2000000,
      gasPrice: 3000000000,
      value: web3.utils.toWei('0.01', 'ether'),
    }).then((contract) => {
      //console.log(contract.options.address) // instance with the new contract address
      if (contract.options.address) {
        this.game = contract;
        // this.subscribe();
        console.log("Deployment successful @ address", contract.options.address);
        contract.events.GameStarted(function () {
          contract.player2(function (err, opponent) {
            this.opponent = opponent;
            this.whoseTurn = this.account;
          });
        });
      } else {
        console.log("Deploying contract. Transaction hash: ", contract.options.transactionHash);
      }

    });
  },

  join: function () {
    const { web3 } = this;

    var address = "0x9F0f68fBe19896C68551205fA732ac12B9C19Fc6";
    GameContract.options.address = address;
    console.log("hello ", GameContract);
    GameContract.methods.join().send({
      value: web3.utils.toWei('0.01', 'ether'),
      from: accounts[1]
    }, (error, transactionHash) => {
      if (error) console.log("JOIN ERROR", error);
      else {
        console.log("JOIN SUCCESS + tHash", transactionHash);
      };
    });
    
  },

  // subscribe: function () {
  //   console.log(this.game.methods);
  //   this.game.methods.(function (err, methods) {
  //     console.log(methods);
  //     // this.fetchContractState();
  //   });

  //   pubnub.subscribe({
  //     channels: ['21-' + this.contract.options.address],
  //   });
  // },




// MetaCoin Contract Functions 
  refreshBalance: async function() {
    const { getBalance } = this.meta.methods;
    const balance = await getBalance(account).call();

    const balanceElement = document.getElementsByClassName("balance")[0];
    balanceElement.innerHTML = balance;
  },

  sendCoin: async function() {
    const amount = parseInt(document.getElementById("amount").value);
    const receiver = document.getElementById("receiver").value;

    this.setStatus("Initiating transaction... (please wait)");

    const { sendCoin } = this.meta.methods;
    await sendCoin(receiver, amount).send({ from: account });

    this.setStatus("Transaction complete!");
    this.refreshBalance();
  },

  setStatus: function(message) {
    const status = document.getElementById("status");
    status.innerHTML = message;
  },
};

window.App = App;

window.addEventListener("load", function() {
  if (window.ethereum) {
    // use MetaMask's provider
    App.web3 = new Web3(window.ethereum);
    window.ethereum.enable(); // get permission to access accounts
  } else {
    console.warn(
      "No web3 detected. Falling back to http://127.0.0.1:9545. You should remove this fallback when you deploy live",
    );
    // fallback - use your fallback strategy (local node / hosted node + in-dapp id mgmt / fail)
    App.web3 = new Web3(
      new Web3.providers.HttpProvider("http://127.0.0.1:9545"),
    );
  }

  App.start();
});
